content.c:
Posts in category '{{ category }}'

{% for post in pagination.posts %}
{% include 'partial_post.html' %}
{% endfor %}

{% if pagination.next_page or pagination.prev_page %}
<div id="navigation">
    {% if pagination.next_page %}
        &larr;&nbsp;<a href="{{ pcurl(pagination.next_page) }}" id="navigation-next">Older Entries</a>
    {% endif %}
    {% if pagination.prev_page %}
        &mdash; <a href="{{ pcurl(pagination.prev_page) }}" id="navigation-next">Newer Entries</a>&nbsp;&rarr;
    {% endif %}
</div>
{% endif %}
